const alphabet = ["a", "b", "c", "d", "e", "f", "g", "h", "i", "j", "k", "l", "m", "n", "o", "p", "q", "r", "s", "t", "u", "v", "w", "x", "y", "z"];


const startButton = document.getElementById("start");
const startBlock = document.getElementById("start-block");
const explanation = document.getElementById("explanation");

const game = document.getElementById("game");
const patternDisplay = document.getElementById("pattern");
const codeInput = document.getElementById("code-input");
const answerInput = document.getElementById("input");
const countdownDisplay = document.getElementById("countdown");
const currentScore = document.getElementById("current-score");
const answersDisplay = document.getElementById("answers");
const codeDisplay = document.getElementById("code-display");

const twitter = document.getElementById("twitter");

const gameOver = document.getElementById("game-over");
const gameOverScore = document.getElementById("game-over-score");
const allAnswers = document.getElementById("all-answers");

let timer;

let possibleAnswers = [];
const answers = [];
let score = 0;
let pattern;
let roundCode;
let regex;

const setRoundCode = () => {
  if (codeInput.value != '') {
    if (patterns[parseInt(codeInput.value)]) {
      roundCode = parseInt(codeInput.value);
    } else {
      window.alert("Sorry, code not recognised");
    }
  } else {
    roundCode = Math.floor(Math.random() * patterns.length);
  }
  codeDisplay.innerHTML = roundCode;
}

const setPattern = () => {
  setRoundCode();
  pattern = patterns[roundCode][0];
  regex = new RegExp(pattern, 'i');
  // First replace every \\w (gap) with _
  // Then make all letters uppercase
  // Then add a space between each character
  pattern = pattern.replace(/\\w/g, '_').toUpperCase().split('').join('&nbsp;');;
  patternDisplay.innerHTML = pattern;
};

const displayPossibleAnswers = () => {
  possibleAnswers = words.filter(word => regex.test(word));
  possibleAnswers.forEach((word, index) => {
    if (answers.includes(word)) {
      possibleAnswers[index] = `<strong>${word}</strong>`;
    }
  })
  allAnswers.innerHTML = possibleAnswers.join(", ");
}

const timerCallback = () => {
  if (countdownDisplay.value < 10) {
    countdownDisplay.value += 0.01;
  } else {
    clearInterval(timer);
    game.hidden = true;
    gameOverScore.innerText = score;
    startBlock.hidden = false;
    gameOver.hidden = false;
    twitter.innerHTML = `<a class="twitter-share-button" href="https://twitter.com/intent/tweet?text=I%20scored%20${score}!%20Can%20you%20beat%20me?%20Enter%20round%20code%20${roundCode}%20here:&url=www.robowenking.com/gaps"><i class="fab fa-twitter"></i></a>`
    displayPossibleAnswers();
  }
}

const startTimer = () => {
  timer = window.setInterval(timerCallback, 10)
}

const startRound = () => {
  startBlock.hidden = true;
  explanation.hidden = true;
  game.hidden = false;
  gameOver.hidden = true;
  setPattern();
  score = 0;
  answerInput.value = "";
  countdownDisplay.value = 0;
  currentScore.innerHTML = "0";
  answersDisplay.innerHTML = "";
  startTimer();
};

const isValidAnswer = (answer) => {
  return regex.test(answer) && words.includes(answer);
};

const handleCorrectInput = (answer) => {
  if (!answers.includes(answer)) {
    answersDisplay.insertAdjacentHTML('afterbegin', `<div>${answer}</div>`);
    answerInput.value = "";
    clearInterval(timer);
    score += 100;
    score += Math.floor(10 * (10 - countdownDisplay.value));
    currentScore.innerHTML = score;
    countdownDisplay.value = 0;
    startTimer();
  }

  answers.push(answer);
};

startButton.addEventListener('click', (event) => {
  startRound();
})

answerInput.addEventListener('input', (event) => {
  const answer = answerInput.value.toLowerCase();
  if (isValidAnswer(answer)) { handleCorrectInput(answer); }
})
